import React, { useState, useRef } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, Button } from "react-bootstrap";

const FormDishes = () => {
  const [formDish, setFormDish] = useState({
    plate: "",
    description: "",
    category: "",
    price: "",
  });

  const inputFileRef = useRef();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormDish({ ...formDish, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formDish);
    console.log(Button);
    console.log(inputFileRef.current.files);
  };

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <Form
            style={{
              padding: "25px 0",
            }}
            onSubmit={handleSubmit}
          >
            <Form.Group controlId="formGridAddress">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Foto del platillo
              </Form.Label>
              <Form.File
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="plate"
                type="file"
                ref={inputFileRef}
              />
            </Form.Group>

            <Form.Group controlId="formGridAddress2">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Añade una breve descripción del platillo
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="description"
                type="text"
                value={formDish.description}
                onChange={handleChange}
                placeholder="Ingresa la descripción"
              />
            </Form.Group>
            <Form.Group controlId="formGridReference">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Añade una categoría
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="category"
                type="text"
                value={formDish.category}
                onChange={handleChange}
                placeholder="Ej. Chilaquiles"
              />
            </Form.Group>
            <Form.Group controlId="formGridAlias">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Precio del platillo
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="price"
                type="number"
                value={formDish.price}
                onChange={handleChange}
                placeholder="Ingresa precio"
              />
            </Form.Group>
          </Form>
          <Button
            className="button"
            style={{
              backgroundColor: "#fb923c",
              border: "none",
              padding: "12px 100px",
            }}
            size="lg"
            type="submit"
          >
            Siguiente
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default FormDishes;
