import React, { Component } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { ListGroup } from "react-bootstrap";
import HeaArr from "../../components/HeaArr";
import axios from "axios";
import edit from "../../assets/icons/edit.svg";
import trash from "../../assets/icons/delete.svg";

const url =
  "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/restaurant/1/menus/";

class MenuList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      getDishes: [],
    };
  }
  componentDidMount() {
    axios
      .get(
        url
        // "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/restaurant/1/menus/"
      )
      .then((response) => {
        console.log(response.data.menus);
        this.setState({ getDishes: response.data.menus });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <HeaArr />
          <Col>
            <h3
              style={{
                fontFamily: "GothamBold",
                display: "flex",
                justifyContent: "center",
                marginBottom: "25px",
              }}
            >
              Mis men√∫s
            </h3>

            <ListGroup variant="flush">
              {this.state.getDishes.map((e) => (
                <ListGroup.Item
                  key={e.id}
                  value={e.id}
                  style={{
                    width: "100%",
                    display: "flex",
                    flexDirection: "row",
                  }}
                >
                  {e.title} ...........................
                  <span style={{ alignContent: "space-around" }}>
                    ${e.price}
                    .00
                  </span>
                  <div>
                    <img src={edit}></img>
                    <img src={trash}></img>
                  </div>
                </ListGroup.Item>
              ))}
            </ListGroup>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MenuList;
