import React, { Component } from "react";
import swal from "sweetalert";

function mensaje() {
  swal({
    title: "Título",
    text: "Mensaje de texto",
    html: "<p>Mensaje de texto con <strong>formato</strong>.</p>",
    type: "success",
    timer: 3000,
  });
}

export default Test;
