import axios from "axios";

const RUTA_BASE = "https://localhost:3000/api/";
const API_URL = "http://localhost:8000/api/";

function getRestaurants() {
  const URL = `${RUTA_BASE}restaurants`;
  const options = {
    method: "GET",
  };
  return fetch(URL, options);
}
// retorna una promesa

export { getRestaurants };

//FORM SIGNUP
export async function signupForm(userData) {
  try {
    // console.log(userData);
    const formData = new FormData();

    formData.append("first_name", userData.first_name);
    formData.append("last_name", userData.last_name);
    formData.append("email", userData.email);
    formData.append("password", userData.password);
    formData.append("phone", userData.phone);

    const response = await axios({
      url:
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/client/create/",
      method: "POST",
      data: formData,
    });

    return response;
  } catch (e) {
    console.log(e);
  }
}

//FORM DIRECTION INFO
export async function formRest(userData) {
  try {
    // console.log(userData);
    const formData = new FormData();

    formData.append("name", userData.name);
    formData.append("street", userData.street);
    formData.append("ext_number", userData.ext_number);
    formData.append("int_number", userData.int_number);
    formData.append("zip_code", userData.zip_code);
    formData.append("municipality", userData.municipality);
    formData.append("suburb", userData.suburb);
    formData.append("link_google", userData.link_google);

    const response = await axios({
      url: "http://127.0.0.1:8000/api/restaurant/create/",
      method: "POST",
      data: formData,
    });

    return response;
  } catch (e) {
    console.log(e);
  }
}

export async function createDishes(dishesData) {
  try {
    // console.log(userData);
    const formData = new FormData();

    formData.append("name", dishesData.name);
    formData.append("image", dishesData.image);
    formData.append("price", dishesData.price);
    formData.append("type", dishesData.type);

    const response = await axios({
      url: "http://127.0.0.1:8000/api/plate/create/",
      method: "POST",
      data: formData,
    });

    return response;
  } catch (e) {
    console.log(e);
  }
}
