import React, { Component } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form } from "react-bootstrap";
import "./styles.css";
import axios from "axios";
import CustomButton from "../Button";

// import { getDishes } from "../../services/restaurants";

class MenuRestaurant extends Component {
  constructor(props) {
    super(props);

    this.state = {
      first: [],
      second: [],
      third: [],
      fourth: [],
      drinks: [],
      complement: [],
      extra: [],
      tacos: [],
      snack: [],
    };
  }

  componentDidMount() {
    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/filter"
      )
      .then((response) => {
        //console.log(response.data.plates.primer_tiempo);
        this.setState({ first: response.data.plates.primer_tiempo });
        this.setState({ second: response.data.plates.segundo_tiempo });
        this.setState({ third: response.data.plates.tercer_tiempo });
        this.setState({ fourth: response.data.plates.cuarto_tiempo });
        this.setState({ drinks: response.data.plates.bebidas });
        this.setState({ complement: response.data.plates.Complementos });
        this.setState({ extra: response.data.plates.ingredientes_extras });
        this.setState({ tacos: response.data.plates.tacos });
        this.setState({ snack: response.data.plates.snack });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <Form
              style={{
                padding: "25px 0",
                marginTop: "20px",
              }}
            >
              <Form.Group controlId="formGridReference">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Nombre del men√∫
                </Form.Label>
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="name"
                  type="text"
                  placeholder="Ingresa el nombre"
                />
              </Form.Group>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Primer tiempo</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.first.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Segundo tiempo</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.second.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Tercer tiempo</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.third.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Cuarto tiempo</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.fourth.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Bebidas</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.drinks.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Complementos</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.complement.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Ingredientes extra</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.extra.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Tacos</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.tacos.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Snack</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.snack.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <CustomButton className="button" type="submit">
                Guardar
              </CustomButton>
            </Form>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MenuRestaurant;
