import React, { Component } from "react";
import { Container, Row, Col } from "react-bootstrap";
import Select from "react-select";
import { Form } from "react-bootstrap";
import "./styles.css";
import axios from "axios";
import CustomButton from "../Button";

class MenuRestaurant extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      first: [],
      second: [],
      third: [],
      fourth: [],
      drinks: [],
      complement: [],
      extra: [],
      tacos: [],
      snack: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    this.setState({
      [e.target.value]: e.target.value,
    });
    // console.log(e.target.value);
  }

  handleSubmit(e) {
    e.preventDefault();
    console.log(this.state);
  }

  componentDidMount() {
    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/filter"
      )
      .then((response) => {
        //console.log(response.data.plates.primer_tiempo);
        this.setState({ first: response.data.plates.primer_tiempo });
        this.setState({ second: response.data.plates.segundo_tiempo });
        this.setState({ third: response.data.plates.tercer_tiempo });
        this.setState({ fourth: response.data.plates.cuarto_tiempo });
        this.setState({ drinks: response.data.plates.bebidas });
        this.setState({ complement: response.data.plates.Complementos });
        this.setState({ extra: response.data.plates.ingredientes_extras });
        this.setState({ tacos: response.data.plates.tacos });
        this.setState({ snack: response.data.plates.snack });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <Form
              onSubmit={this.handleSubmit}
              style={{
                padding: "25px 0",
                marginTop: "20px",
              }}
            >
              <Form.Group controlId="formGridReference">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Nombre del menú
                </Form.Label>
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="title"
                  type="text"
                  placeholder="Ingresa el nombre"
                  onChange={this.handleChange}
                />
              </Form.Group>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Primer tiempo</h5>
              </div>

              {/* <select
                name="first"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.first.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select> */}
              <Select
                name="form-field-name"
                value={this.state.value}
                onChange={this.handleChange}
                labelKey={"primer_tiempo"}
                valueKey={"primer_tiempo"}
                options={this.state.first}
              />
              <div style={{ maxWidth: "350px", margin: "0 auto" }}>
                <StyledSelect
                  placeholder="Select peoples"
                  addPlaceholder={this.state.addPlaceholder}
                  color={this.state.color}
                  disabled={this.state.disabled}
                  loading={this.state.loading}
                  searchBy={this.state.searchBy}
                  separator={this.state.separator}
                  clearable={this.state.clearable}
                  searchable={this.state.searchable}
                  create={this.state.create}
                  keepOpen={this.state.forceOpen}
                  dropdownHandle={this.state.handle}
                  dropdownHeight={this.state.dropdownHeight}
                  direction={this.state.direction}
                  multi={this.state.multi}
                  values={[options.find((opt) => opt.username === "Delphine")]}
                  labelField={this.state.labelField}
                  valueField={this.state.valueField}
                  options={options}
                  dropdownGap={5}
                  keepSelectedInList={this.state.keepSelectedInList}
                  onDropdownOpen={() => undefined}
                  onDropdownClose={() => undefined}
                  onClearAll={() => undefined}
                  onSelectAll={() => undefined}
                  onChange={(values) => this.setValues(values)}
                  noDataLabel="No matches found"
                  closeOnSelect={this.state.closeOnSelect}
                  noDataRenderer={
                    this.state.noDataRenderer
                      ? () => this.noDataRenderer()
                      : undefined
                  }
                  dropdownPosition={this.state.dropdownPosition}
                  itemRenderer={
                    this.state.itemRenderer
                      ? (item, itemIndex, props, state, methods) =>
                          this.itemRenderer(
                            item,
                            itemIndex,
                            props,
                            state,
                            methods
                          )
                      : undefined
                  }
                  inputRenderer={
                    this.state.inputRenderer
                      ? (props, state, methods) =>
                          this.inputRenderer(props, state, methods)
                      : undefined
                  }
                  optionRenderer={
                    this.state.optionRenderer
                      ? (option, props, state, methods) =>
                          this.optionRenderer(option, props, state, methods)
                      : undefined
                  }
                  contentRenderer={
                    this.state.contentRenderer
                      ? (innerProps, innerState) =>
                          this.contentRenderer(innerProps, innerState)
                      : undefined
                  }
                  dropdownRenderer={
                    this.state.dropdownRenderer
                      ? (innerProps, innerState, innerMethods) =>
                          this.dropdownRenderer(
                            innerProps,
                            innerState,
                            innerMethods
                          )
                      : undefined
                  }
                />
              </div>

              {/*  */}
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Segundo tiempo</h5>
              </div>

              <select
                name="second"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.second.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Tercer tiempo</h5>
              </div>

              <select
                name="third"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.third.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Cuarto tiempo</h5>
              </div>

              <select
                name="fourth"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.fourth.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Bebidas</h5>
              </div>

              <select
                name="drinks"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.drinks.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Complementos</h5>
              </div>

              <select
                name="complement"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.complement.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Ingredientes extra</h5>
              </div>

              <select
                name="extra"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.extra.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Tacos</h5>
              </div>

              <select
                name="tacos"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.tacos.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Snack</h5>
              </div>

              <select
                name="snack"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.snack.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <CustomButton className="button" type="submit">
                Guardar
              </CustomButton>
            </Form>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MenuRestaurant;
