import React, { Component, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, InputGroup, FormControl } from "react-bootstrap";
import "./styles.css";
import axios from "axios";
import CustomButton from "../Button";

const MenuRestaurant = () => {
  const [dishes, setDishes] = useState({
    first: [],
    second: [],
    third: [],
    fourth: [],
    drinks: [],
    complement: [],
    extra: [],
    tacos: [],
    snack: [],
  });

  const [dataMenu, setDataMenu] = useState({
    title: "",
    price: "",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDishes({ ...dishes, [name]: value });
  };

  const _handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.state);
    handleSubmit({ ...dishes });
  };

  // componentDidMount() {
  //   axios
  //     .get(
  //       "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/filter"
  //     )
  //     .then((response) => {
  //       //console.log(response.data.plates.primer_tiempo);
  //       this.setState({ first: response.data.plates.primer_tiempo });
  //       this.setState({ second: response.data.plates.segundo_tiempo });
  //       this.setState({ third: response.data.plates.tercer_tiempo });
  //       this.setState({ fourth: response.data.plates.cuarto_tiempo });
  //       this.setState({ drinks: response.data.plates.bebidas });
  //       this.setState({ complement: response.data.plates.Complementos });
  //       this.setState({ extra: response.data.plates.ingredientes_extras });
  //       this.setState({ tacos: response.data.plates.tacos });
  //       this.setState({ snack: response.data.plates.snack });
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });
  // }

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <Form
            onSubmit={this.handleSubmit}
            style={{
              padding: "25px 0",
              marginTop: "20px",
            }}
          >
            <Form.Group controlId="formGridReference">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Nombre del menú
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="title"
                type="text"
                placeholder="Ingresa el nombre"
                onChange={this.handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formGridAlias" style={{ margin: "15px 0" }}>
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Precio del menú
              </Form.Label>
              <InputGroup>
                <InputGroup.Prepend>
                  <InputGroup.Text
                    style={{
                      fontFamily: "GothamBook",
                      fontSize: "12px",
                      height: " 34px",
                      background: "none",
                    }}
                  >
                    $
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  aria-label="Amount "
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="price"
                  type="number"
                  onChange={this.handleChange}
                  placeholder="Ingresa precio del menú"
                />
                <InputGroup.Append>
                  <InputGroup.Text
                    style={{
                      fontFamily: "GothamBook",
                      fontSize: "12px",
                      height: " 34px",
                      background: "none",
                    }}
                  >
                    .00
                  </InputGroup.Text>
                </InputGroup.Append>
              </InputGroup>
            </Form.Group>

            <Form.Group controlId="formGridReference">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Describe tu menú
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="description"
                type="text"
                placeholder="Describe los tiempos que tiene tu menú"
                onChange={this.handleChange}
              />
            </Form.Group>

            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Primer tiempo</h5>
            </div>

            <select
              name="first"
              className="form-control"
              onChange={this.handleChange}
              multiple
              style={{ border: "none" }}
            >
              {this.state.first.map((e) => (
                <option key={e.plates} value={e.plates} type="checkbox">
                  {e.name}
                </option>
              ))}
            </select>

            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Segundo tiempo</h5>
            </div>

            <select
              name="second"
              className="form-control"
              onChange={this.handleChange}
            >
              {this.state.second.map((e) => (
                <option key={e.id} value={e.id}>
                  {e.name}
                </option>
              ))}
            </select>

            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Tercer tiempo</h5>
            </div>

            <select
              name="third"
              className="form-control"
              onChange={this.handleChange}
            >
              {this.state.third.map((e) => (
                <option key={e.id} value={e.id}>
                  {e.name}
                </option>
              ))}
            </select>

            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Cuarto tiempo</h5>
            </div>

            <select
              name="fourth"
              className="form-control"
              onChange={this.handleChange}
            >
              {this.state.fourth.map((e) => (
                <option key={e.id} value={e.id}>
                  {e.name}
                </option>
              ))}
            </select>

            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Bebidas</h5>
            </div>

            <select
              name="drinks"
              className="form-control"
              onChange={this.handleChange}
            >
              {this.state.drinks.map((e) => (
                <option key={e.id} value={e.id}>
                  {e.name}
                </option>
              ))}
            </select>

            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Complementos</h5>
            </div>

            <select
              name="complement"
              className="form-control"
              onChange={this.handleChange}
            >
              {this.state.complement.map((e) => (
                <option key={e.id} value={e.id}>
                  {e.name}
                </option>
              ))}
            </select>

            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Ingredientes extra</h5>
            </div>

            <select
              name="extra"
              className="form-control"
              onChange={this.handleChange}
            >
              {this.state.extra.map((e) => (
                <option key={e.id} value={e.id}>
                  {e.name}
                </option>
              ))}
            </select>

            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Tacos</h5>
            </div>

            <select
              name="tacos"
              className="form-control"
              onChange={this.handleChange}
            >
              {this.state.tacos.map((e) => (
                <option key={e.id} value={e.id}>
                  {e.name}
                </option>
              ))}
            </select>
            <div className="gray-bar">
              <h5 style={{ fontSize: "16px" }}>Snack</h5>
            </div>

            <select
              name="snack"
              className="form-control"
              onChange={this.handleChange}
            >
              {this.state.snack.map((e) => (
                <option key={e.id} value={e.id}>
                  {e.name}
                </option>
              ))}
            </select>

            <CustomButton className="button" type="submit">
              Guardar
            </CustomButton>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default MenuRestaurant;
