	const [ isClick, setIsClick ] = useState(false);
	const [ restaurants, setRestaurants ] = useState([
		{
			name: 'Casa To침o',
			address: 'Av. del Iman 50',
			paymentMethod: {
				debitcard: 'yes',
				creditcard: 'no',
				cash: 'yes'
			}
		},
		{
			name: 'Las Pichanchas'
		}
	]);

	const handleOnClick = async (e) => {
		console.log(e.target);
		console.log('Clicked');
		setIsClick(!isClick);
		// const response = await getRestaurants();
