import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import { ListGroup, InputGroup, FormControl } from "react-bootstrap";
import HeaArr from "../../components/HeaArr";
import axios from "axios";
import edit from "../../assets/icons/edit.svg";
import trash from "../../assets/icons/delete.svg";

import { useParams } from "react-router-dom";

const OrderList = (props) => {
  const [data, setData] = useState({});
  const { id: idURL } = useParams();

  useEffect(() => {
    axios
      .get(url(idURL))
      .then((response) => {
        console.log(response.data);
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const url = (id) =>
    `http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/order/${id}/detail/`;

  const updateStatus = (status) => {
    let nextStatus;
    if (status === "recibido") {
      nextStatus = "preparando";
    } else if (status === "preparando") {
      nextStatus = "enviado";
    } else if (status === "enviado") {
      //   nextStatus = "entregado";
      // } else if (status === "entregado") {
      // }
    } else {
      nextStatus = "entregado";
    }

    axios
      .patch(
        `http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/order/${idURL}/update/`,
        { status: nextStatus }
      )
      .then((response) => {
        console.log(response.data);
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  console.log(data);
  return (
    <Container>
      <Row className="justify-content-md-center">
        <HeaArr />
        <Col>
          <h3
            style={{
              fontFamily: "GothamBold",
              display: "flex",
              justifyContent: "center",
              marginBottom: "10px",
            }}
          >
            Orden {data.id}
          </h3>
          <span
            style={{
              fontFamily: "GothamBold",
              display: "flex",
              justifyContent: "center",
              marginBottom: "25px",
              color: "rgb(140 140 140)",
            }}
          >
            {Object.keys(data).length ? data.name : null}
          </span>

          <h5>
            Menu
            <span
              style={{
                fontFamily: "GothamBold",
                display: "flex",
                justifyContent: "center",
                marginBottom: "30px",
              }}
            >
              {Object.keys(data).length ? data.menus[0].title : null}
            </span>
          </h5>
          <ListGroup variant="flush">
            {Object.keys(data).length
              ? data.plates.map(({ id, name }) => (
                  <ListGroup.Item
                    key={id}
                    value={id}
                    style={{
                      width: "100%",
                      display: "flex",
                      flexDirection: "row",
                      justifyContent: "center",
                    }}
                  >
                    <InputGroup>
                      <InputGroup.Checkbox />
                    </InputGroup>
                    {name}
                  </ListGroup.Item>
                ))
              : null}
          </ListGroup>

          <div
            style={{
              display: "flex",
              flexDirection: "row",
              alignItems: "baseline",
            }}
          >
            <h5>Estatus: </h5>

            <p style={{ color: "#2FB423", fontSize: "20px", padding: "10px" }}>
              {data.status}
            </p>
          </div>

          <Button
            onClick={() => updateStatus()}
            style={{
              backgroundColor: "#fb923c",
              border: "none",
              padding: "12px 50px",
              fontFamily: "GothamBold",
              fontSize: "20px",
              display: "flex",
              margin: "auto",
            }}
          >
            Actualizar estatus
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default OrderList;
