import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, Button } from "react-bootstrap";

const EditForm = ({ handleSubmit }) => {
  const [formValues, setFormValues] = useState({
    street: "",
    ext_number: "",
    int_number: "",
    zip_code: "",
    municipality: "",
    suburb: "",
    references: "",
    alias: "",
    phone: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    // console.log(name, value);
    setFormValues({ ...formValues, [name]: value });
  };

  const _handleSubmit = (e) => {
    e.preventDefault();
    handleSubmit({ ...formValues });
    // console.log(formValues);
  };

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <Form
            style={{
              padding: "25px 0",
            }}
            onSubmit={_handleSubmit}
          >
            <Form.Group controlId="formGridAddress">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Nombres
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="street"
                type="text"
                value={formValues.street}
                onChange={handleChange}
                placeholder="Ingresa tu dirección"
              />
            </Form.Group>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridNoExt">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Apellidos
                </Form.Label>
                <Form.Control
                  style={{
                    fontSize: "13px",
                  }}
                  name="ext_number"
                  type="number"
                  value={formValues.ext_number}
                  onChange={handleChange}
                />
              </Form.Group>

              <Form.Group controlId="formGridPhoneNum">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Número celular
                </Form.Label>
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="phone"
                  type="number"
                  value={formValues.phone}
                  onChange={handleChange}
                  placeholder="(55) 5555-5555"
                />
              </Form.Group>

              <Form.Group>
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Correo electrónico
                </Form.Label>
                <Form.Control
                  style={{
                    fontSize: "13px",
                  }}
                  name="zip_code"
                  type="number"
                  value={formValues.zip_code}
                  onChange={handleChange}
                />
              </Form.Group>
            </Form.Row>

            <Button
              className="button"
              style={{
                backgroundColor: "#fb923c",
                border: "none",
                padding: "12px 100px",
              }}
              size="lg"
              type="submit"
            >
              Guardar
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default EditForm;
