import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, Button } from "react-bootstrap";

const FormDirection = ({ handleSubmit }) => {
  const [street, setStreet] = useState("");
  const [ext_number, setExt_number] = useState("");
  const [int_number, setInt_number] = useState("");
  const [zip_code, setZip_code] = useState("");
  const [municipality, setMunicipality] = useState("");
  const [suburb, setSuburb] = useState("");
  const [references, setReferences] = useState("");
  const [alias, setAlias] = useState("");
  const [phone, setPhone] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    // console.log(name, value);
    setStreet({ ...street, [name]: value });
    setExt_number({ ...ext_number, [name]: value });
    setInt_number({ ...int_number, [name]: value });
    setZip_code({ ...zip_code, [name]: value });
    setMunicipality({ ...municipality, [name]: value });
    setSuburb({ ...suburb, [name]: value });
    setReferences({ ...references, [name]: value });
    setAlias({ ...alias, [name]: value });
    setPhone({ ...phone, [name]: value });
  };

  const _handleSubmit = (e) => {
    e.preventDefault();
    handleSubmit({ ...street });
    handleSubmit({ ...ext_number });
    handleSubmit({ ...int_number });
    handleSubmit({ ...zip_code });
    handleSubmit({ ...municipality });
    handleSubmit({ ...suburb });
    handleSubmit({ ...references });
    handleSubmit({ ...alias });
    handleSubmit({ ...phone });
    // console.log(formValues);
  };

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <Form
            style={{
              padding: "25px 0",
            }}
            onSubmit={_handleSubmit}
          >
            <Form.Group controlId="formGridAddress">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Calle
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="street"
                type="text"
                value={street.street}
                onChange={handleChange}
                placeholder="Ingresa tu dirección "
              />
            </Form.Group>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridNoExt">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  No. Ext
                </Form.Label>
                <Form.Control
                  style={{
                    fontSize: "13px",
                  }}
                  type="number"
                  value={ext_number}
                  onChange={handleChange}
                />
              </Form.Group>

              <Form.Group as={Col} controlId="formGridNoInt">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  No. Int
                </Form.Label>
                <Form.Control
                  style={{
                    fontSize: "13px",
                  }}
                  type="number"
                  value={int_number}
                  onChange={handleChange}
                />
              </Form.Group>

              <Form.Group as={Col} controlId="formGridCp">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  C.P.
                </Form.Label>
                <Form.Control
                  style={{
                    fontSize: "13px",
                  }}
                  type="number"
                  value={zip_code}
                  onChange={handleChange}
                />
              </Form.Group>
            </Form.Row>
            <Form.Group controlId="formGridMunicipality">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Delegación o municipio
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                type="text"
                value={municipality}
                onChange={handleChange}
                placeholder="Ingresa tu delegación"
              />
            </Form.Group>
            <Form.Group controlId="formGridAddress2">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Colonia
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                type="text"
                value={suburb}
                onChange={handleChange}
                placeholder="Ingresa tu colonia"
              />
            </Form.Group>
            <Form.Group controlId="formGridReference">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Referencias y/o entre calles
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                type="text"
                value={references}
                onChange={handleChange}
                placeholder="Ingresa tus referencias"
              />
            </Form.Group>
            <Form.Group controlId="formGridAlias">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Escribe un alias
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                type="text"
                value={alias}
                onChange={handleChange}
                placeholder="Ingresa un alias"
              />
            </Form.Group>
            <Form.Group controlId="formGridPhoneNum">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Número celular
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                type="number"
                value={phone}
                onChange={handleChange}
                placeholder="(55) 5555-5555"
              />
            </Form.Group>
            <Button
              className="button"
              style={{
                backgroundColor: "#fb923c",
                border: "none",
                padding: "12px 100px",
              }}
              size="lg"
              type="submit"
            >
              Guardar
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default FormDirection;
