import React, { Component } from "react";
import Select from "react-select";
import { Container, Row, Col } from "react-bootstrap";
import { Form } from "react-bootstrap";
// import CustomButton from "../Button";

import { getDishes } from "../../services/restaurants";

class MenuRestaurant extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectDishes: [],
      clearable: true,
      dishes: [],
    };
  }

  componentDidMount() {
    getDishes().then((res) => {
      this.setState({
        dishes: res.dishes,
      });
      console.log("algo", this.state.dishes);
    });
  }

  handleChange(selectDishes) {
    this.setState({ selectDishes });
  }

  render() {
    // let options = this.state.dishes.map(function (dishes) {
    //   return { value: dishes.type, label: dishes.name };
    // });
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <Form
              style={{
                padding: "25px 0",
                marginTop: "20px",
              }}
            >
              <Form.Group controlId="formGridReference">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Nombre del men√∫
                </Form.Label>
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="name"
                  type="text"
                  placeholder="Ingresa el nombre"
                />
              </Form.Group>
              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 40px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}> Primer tiempo</h5>
              </div>
              <Select
                name="test"
                value={this.state.value}
                onChange={this.handleChange}
                clearable={this.state.clearable}
                searchable={this.state.searchable}
                labelKey={"name"}
                valueKey={"type"}
                options={this.state.dishes}
              />
              {/* <CustomButton className="button" type="submit">
                Guardar
              </CustomButton> */}
            </Form>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MenuRestaurant;
