import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './style.css';
//import { getMenus } from '../../services/menus';
import MenuModal from '../../components/menuModal';

const SubMenu = () => {
	const handleOnClick = async (e) => {
		console.log(e.target);
		console.log(`Clicked`);
	};

	return (
		<div>
			<div className="carrousel-menu col-12">
				<div className="scrollmenu ml-2 col-12 justify-content-center">
					<ul className="nav nav-pills nav-fill">
						<li className="nav-item">
							<button className="navlink customB" id="Sub-1" onClick={handleOnClick}>
								Combo 1
							</button>
						</li>

						<li className="nav-item">
							<button className="navlink customB" id="Sub-2" onClick={handleOnClick}>
								Combo 2
							</button>
						</li>

						<li className="nav-item">
							<button className="navlink customB" id="Sub-3" onClick={handleOnClick}>
								Combo 3
							</button>
						</li>
					</ul>
				</div>
			</div>

			{/* Contenido de cada TAB */}

			<div className="tab-content col-12" id="TabContentWrapper" />
			<MenuModal />
		</div>
	);
};

export default SubMenu;
