import React, { useState, useRef } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, Button, InputGroup, FormControl } from "react-bootstrap";

const FormDishes = () => {
  const [formDish, setFormDish] = useState({
    plate: "",
    description: "",
    category: "",
    price: "",
  });

  const inputFileRef = useRef();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormDish({ ...formDish, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formDish);
    console.log(inputFileRef.current.files);
  };

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <Form
            style={{
              padding: "25px 0",
              marginTop: "20px",
            }}
            onSubmit={handleSubmit}
          >
            <Form.Group controlId="formGridAddress">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Foto del platillo
              </Form.Label>
              <Form.File
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="plate"
                type="file"
                ref={inputFileRef}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formGridAddress2">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Añade una breve descripción del platillo
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="description"
                type="text"
                value={formDish.description}
                onChange={handleChange}
                placeholder="Ingresa la descripción"
              />
            </Form.Group>
            <Form.Group controlId="formGridReference">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Añade una categoría
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="category"
                type="text"
                value={formDish.category}
                onChange={handleChange}
                placeholder="Ej. Chilaquiles"
              />
            </Form.Group>
            <Form.Group controlId="formGridAlias">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Precio del platillo
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="price"
                type="number"
                value={formDish.price}
                onChange={handleChange}
                placeholder="Ingresa precio"
              />
            </Form.Group>

            <InputGroup className="">
              <InputGroup.Prepend>
                <InputGroup.Text
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                    height: " 30px",
                  }}
                >
                  $
                </InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                aria-label="Amount "
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="price"
                type="number"
                value={formDish.price}
                onChange={handleChange}
                placeholder="Ingresa precio"
              />
              <InputGroup.Append>
                <InputGroup.Text>.00</InputGroup.Text>
              </InputGroup.Append>
            </InputGroup>
            <Button
              className="button"
              style={{
                backgroundColor: "#fb923c",
                border: "none",
                marginTop: "100px",
                padding: "12px 100px",
              }}
              size="lg"
              type="submit"
            >
              Siguiente
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default FormDishes;
