import React, { Component, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form } from "react-bootstrap";
import "./styles.css";
import axios from "axios";
import CustomButton from "../Button";
import { Multiselect } from "multiselect-react-dropdown";

class MenuRestaurant extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      first: [],
      second: [],
      third: [],
      fourth: [],
      drinks: [],
      complement: [],
      extra: [],
      tacos: [],
      snack: [],
      selected: false,
      selectedOptions: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.onOptionsChange = this.onOptionsChange.bind(this);
  }

  handleChange(e) {
    this.setState({
      [e.target.value]: e.target.value,
    });
    console.log(e.target.value);
  }

  handleSubmit(e) {
    e.preventDefault();
    console.log(this.state);
  }

  onOptionsChange(option, selected) {
    if (selected) {
      this.setState(
        (prevState) => {
          prevState.selectedOptions.push(option);
          return {
            selectedOptions: prevState.selectedOptions,
          };
        },
        () => this.props.onOptionsChange(this.state.selectedOptions)
      );
    } else {
      this.setState(
        (prevState) => {
          const index = prevState.selectedOptions.indexOf(option);
          if (index > -1) {
            prevState.selectedOptions.splice(index, 1);
          }
          return {
            selectedOptions: prevState.selectedOptions,
          };
        },
        () => this.props.onOptionsChange(this.state.selectedOptions)
      );
    }
  }

  onOptionsChange(selectedOptions) {
    this.setState({ selectedOptions: selectedOptions });
  }

  componentDidMount() {
    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/filter"
      )
      .then((response) => {
        //console.log(response.data.plates.primer_tiempo);
        this.setState({ first: response.data.plates.primer_tiempo });
        this.setState({ second: response.data.plates.segundo_tiempo });
        this.setState({ third: response.data.plates.tercer_tiempo });
        this.setState({ fourth: response.data.plates.cuarto_tiempo });
        this.setState({ drinks: response.data.plates.bebidas });
        this.setState({ complement: response.data.plates.Complementos });
        this.setState({ extra: response.data.plates.ingredientes_extras });
        this.setState({ tacos: response.data.plates.tacos });
        this.setState({ snack: response.data.plates.snack });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <Form
              onSubmit={this.handleSubmit}
              style={{
                padding: "25px 0",
                marginTop: "20px",
              }}
            >
              <Form.Group controlId="formGridReference">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Nombre del menú
                </Form.Label>
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="title"
                  type="text"
                  placeholder="Ingresa el nombre"
                  onChange={this.handleChange}
                />
              </Form.Group>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Primer tiempo</h5>
              </div>

              <select
                name="first"
                className="form-control"
                onChange={this.handleChange}
                multiple="multiple"
                onOptionChange={this.onOptionsChange}
              >
                {this.state.first.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
                <p>Selected Options: {this.state.selectedOptions + ""}</p>
              </select>
              <p>Selected Options: {this.state.selectedOptions + ""}</p>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Segundo tiempo</h5>
              </div>

              <select
                name="second"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.second.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Tercer tiempo</h5>
              </div>

              <select
                name="third"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.third.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Cuarto tiempo</h5>
              </div>

              <select
                name="fourth"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.fourth.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Bebidas</h5>
              </div>

              <select
                name="drinks"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.drinks.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Complementos</h5>
              </div>

              <select
                name="complement"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.complement.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Ingredientes extra</h5>
              </div>

              <select
                name="extra"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.extra.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Tacos</h5>
              </div>

              <select
                name="tacos"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.tacos.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div className="gray-bar">
                <h5 style={{ fontSize: "16px" }}>Snack</h5>
              </div>

              <select
                name="snack"
                className="form-control"
                onChange={this.handleChange}
              >
                {this.state.snack.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <CustomButton className="button" type="submit">
                Guardar
              </CustomButton>
            </Form>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MenuRestaurant;
