import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import SignUp from "../../components/UserSignUp";

import LoginGoogle from "../../components/GoogleLogin";
import { signupForm } from "../../services/restaurants";

const RestSignup = () => {
  const handleSubmit = (data) => {
    signupForm(data);
  };
  return (
    <div>
      <h1 className="header-rest">A comer!</h1>
      <h3
        style={{
          fontFamily: "GothamBold",
          display: "flex",
          justifyContent: "center",
          fontSize: "24px",
          margin: "20px",
          paddingTop: "20px",
        }}
      >
        Regístra tu restaurante o puesto de comida
      </h3>
      <div
        style={{
          fontFamily: "GothamBook",
          fontSize: "14px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <p
          style={{
            fontFamily: "GothamBook",
            fontSize: "15px",
            marginBottom: "0",
          }}
        >
          Si ya registraste tu restaurante
        </p>
        <a
          href="/register"
          style={{
            fontFamily: "GothamBold",
            fontSize: "15px",
            color: "#2FB423",
            textDecorationLine: "underline",
          }}
        >
          Inicia Sesión
        </a>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          margin: "20px",
          ontFamily: "GothamBook",
          fontSize: "15px",
        }}
      >
        <p>
          Con{" "}
          <span style={{ color: "#2FB423", fontFamily: "GothamBold" }}>
            A comer
          </span>{" "}
          puedes crear tu menú, distribuirlo y recibir pedidos
        </p>
        <p
          style={{
            fontFamily: "GothamBold",
            display: "flex",
            justifyContent: "center",
          }}
        >
          Comiena aquí, es gratis
        </p>
        <p>Ingresa los datos del dueño/administrador del restaurante</p>
      </div>

      <LoginGoogle />
      <SignUp handleSubmit={handleSubmit} />
    </div>
  );
};

export default RestSignup;
