import React, { Component } from "react";
import Select from "react-select";
import { Container, Row, Col } from "react-bootstrap";
import { Form } from "react-bootstrap";
import axios from "axios";
import CustomButton from "../Button";

// import { getDishes } from "../../services/restaurants";

class MenuRestaurant extends Component {
  state = {
    first: [],
    second: [],
    third: [],
    fourth: [],
    drinks: [],
    complement: [],
    extra: [],
    tacos: [],
    snack: [],
  };
  // constructor() {
  //   super();
  //   this.state = {
  //     dishes: [],
  //   };
  // }

  componentDidMount() {
    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=primer_tiempo"
      )
      .then((response) => {
        console.log(response);
        this.setState({ first: response.data });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=segundo_tiempo"
      )
      .then((response) => {
        console.log(response);
        this.setState({ second: response.data });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=tercer_tiempo"
      )
      .then((response) => {
        console.log(response);
        this.setState({ third: response.data });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=cuarto_tiempo"
      )
      .then((response) => {
        console.log(response);
        this.setState({ fourth: response.data });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=bebidas"
      )
      .then((response) => {
        console.log(response);
        this.setState({ drinks: response.data });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=Complementos"
      )
      .then((response) => {
        console.log(response);
        this.setState({ complement: response.data });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=ingredientes_extra"
      )
      .then((response) => {
        console.log(response);
        this.setState({ extra: response.data });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=tacos"
      )
      .then((response) => {
        console.log(response);
        this.setState({ tacos: response.data });
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(
        "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/?type=snack"
      )
      .then((response) => {
        console.log(response);
        this.setState({ snack: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <Form
              style={{
                padding: "25px 0",
                marginTop: "20px",
              }}
            >
              <Form.Group controlId="formGridReference">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Nombre del men√∫
                </Form.Label>
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="name"
                  type="text"
                  placeholder="Ingresa el nombre"
                />
              </Form.Group>
              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 50px 0 10px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}>Primer tiempo</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.first.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 30px 0 10px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}>Segundo tiempo</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.second.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 30px 0 10px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}>Tercer tiempo</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.third.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 30px 0 10px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}>Cuarto tiempo</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.fourth.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>

              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 30px 0 10px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}>Bebidas</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.drinks.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 30px 0 10px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}>Complementos</h5>
              </div>

              <select name="dishes" className="form-control">
                {this.state.complement.map((e) => (
                  <option key={e.id} value={e.id}>
                    {e.name}
                  </option>
                ))}
              </select>
              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 30px 0 10px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}>Ingredientes extra</h5>
              </div>

