import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Modal, Button, Badge } from "react-bootstrap";
import CustomButton from "../Button";
import "./styles.css";

class UserModal extends React.Component {
  constructor() {
    super();
    this.state = {
      show: false,
      isActive: false,
    };
  }
  handleModal() {
    this.setState({ show: !this.state.show });
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            <Button
              onClick={() => {
                this.handleModal();
              }}
            >
              Abrir
            </Button>
            <Modal
              show={this.state.show}
              aria-labelledby="contained-modal-title-vcenter"
              centered
            >
              <Modal.Header
                closeButton
                onHide={() => this.handleModal()}
                style={{
                  border: "none",
                }}
              ></Modal.Header>
              <Modal.Title className="modal-user">
                <h1
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    fontFamily: "GothamBold",
                    fontSize: "24px",
                  }}
                >
                  Mi pedido
                </h1>
              </Modal.Title>
              <Modal.Body className="modal-direction">
                <p>¿Quieres que enviemos tu pedido en esta dirección?</p>
                <Button
                  // onClick={() => {
                  //   this.handleModal();
                  // }}
                  style={{
                    backgroundColor: "#F3F4F6",
                    color: "#000000",
                    border: "none",
                    borderRadius: "5px",
                    fontFamily: "GothamBold",
                    fontSize: "14px",
                  }}
                  onMouseEnter={() => "#c83f49"}
                >
                  <h5>Casa</h5>
                  <p
                    style={{
                      fontFamily: "GothamBook",
                      fontSize: "12px",
                    }}
                  >
                    Calle Pedro Moreno no.24, Colonia Lindavista
                  </p>
                </Button>
              </Modal.Body>

              <Modal.Footer
                style={{
                  border: "none",
                }}
              >
                <CustomButton />
              </Modal.Footer>
            </Modal>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default UserModal;
