import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import { ListGroup } from "react-bootstrap";
import HeaArr from "../../components/HeaArr";
import axios from "axios";
import edit from "../../assets/icons/edit.svg";
import trash from "../../assets/icons/delete.svg";

import { useParams } from "react-router-dom";

const OrderList = (props) => {
  const [data, setData] = useState({});
  const { id: idURL } = useParams();

  useEffect(() => {
    axios
      .get(url(idURL))
      .then((response) => {
        console.log(response.data);
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const url = (id) =>
    `http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/order/${id}/detail/`;

  const updateStatus = (status) => {
    let nextStatus;
    if (status === "recibido") {
      nextStatus = "preparando";
    } else if (status === "preparando") {
      nextStatus = "enviando";
    }

    //   axios
    //     .patch(
    //       `http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/order/${id}/update/`,
    //       { status: nextStatus }
    //     )
    //     .then((response) => {
    //       console.log(response.data);
    //       setData(response.data);
    //     })
    //     .catch((error) => {
    //       console.log(error);
    //     });
  };

  console.log(data);
  return (
    <Container>
      <Row className="justify-content-md-center">
        <HeaArr />
        <Col>
          <h3
            style={{
              fontFamily: "GothamBold",
              display: "flex",
              justifyContent: "center",
              marginBottom: "10px",
            }}
          >
            Orden {data.id}
          </h3>
          <span
            style={{
              fontFamily: "GothamBold",
              display: "flex",
              justifyContent: "center",
              marginBottom: "25px",
              color: "rgb(140 140 140)",
            }}
          >
            {Object.keys(data).length ? data.name : null}
          </span>
        </Col>
        <Col lg>
          <h5>
            Menu
            <span
              style={{
                fontFamily: "GothamBold",
                display: "flex",
                justifyContent: "center",
                marginBottom: "10px",
              }}
            >
              {Object.keys(data).length ? data.menus[0] : null}
            </span>
          </h5>
        </Col>
        <Col lg>
          <h5>Status: {data.status}</h5>
          <Button onClick={() => updateStatus()}>Actualizar estatus</Button>
        </Col>
      </Row>
    </Container>
  );
};

export default OrderList;
