import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import "./styles.css";
import profile from "../../assets/icons/profile.svg";
import cart from "../../assets/icons/shoppingCart.svg";

const Header = ({ headerType }) => {
  switch (headerType) {
    case "type-0":
      return (
        <Container>
          <Row className="justify-content-md-center header-div">
            <Col xs={{ span: 6, offset: 2 }}>
              <h1 className="header-name">A comer!</h1>
            </Col>
            <Col xs={4} md={4} className="login">
              <a href to="/login">
                Iniciar sesión
              </a>
            </Col>
          </Row>
        </Container>
      );
    case "type-1":
      return (
        <Container>
          <Row className="justify-content-md-center header-div">
            <Col xs={3} md={3} className="icon-profile">
              <img src={profile}></img>
            </Col>
            <Col xs={5} md={5}>
              <h1 className="header">A comer!</h1>
            </Col>
            <Col xs={4} md={4} className="icon-cart">
              <img src={cart}></img>
            </Col>
          </Row>
        </Container>
      );
    case "type-2":
      return (
        <Container>
          <Row className="justify-content-md-center header-div">
            <Col xs={3} md={3}>
              <img src={arrow} className="icon-arrow"></img>
            </Col>
            <Col xs={5} md={5}>
              <h1 className="header">A comer!</h1>
            </Col>
            <Col xs={4} md={4} className="icon-cart">
              <img src={cart}></img>
            </Col>
          </Row>
        </Container>
      );
    case "type-3":
      return (
        <Container>
          <Row className="justify-content-md-center header-div">
            <Col xs={3} md={3}>
              <img src={arrow} className="icon-arrow"></img>
            </Col>
            <Col xs={5} md={5}>
              <h1 className="header">A comer!</h1>
            </Col>
            <Col xs={4} md={4} className="login">
              <a href>Iniciar sesión</a>
            </Col>
          </Row>
        </Container>
      );
    case "type-4":
      return (
        <Container>
          <Row className="justify-content-md-center header-div">
            <Col xs={3} md={3}>
              <img src={arrow} className="icon-arrow"></img>
            </Col>
          </Row>
        </Container>
      );
  }
};

export default Header;
