import React, { Component } from "react";
import { Container, Row, Col } from "react-bootstrap";
import HeaArr from "../../components/HeaArr";
import axios from "axios";
import Test from "../RestProfile/test";

const url =
  "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/restaurant/1/menus/";

class MenuList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      getDishes: [],
    };
  }
  componentDidMount() {
    axios
      .get(
        url
        // "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/restaurant/1/menus/"
      )
      .then((response) => {
        console.log(response.data.menus);
        this.setState({ getDishes: response.data.menus });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <HeaArr />
              <h3
                style={{
                  fontFamily: "GothamBold",
                  display: "flex",
                  justifyContent: "space-between",
                }}
              >
                Mis men√∫s
              </h3>

              {this.state.getDishes.map((e) => (
                <p key={e.id} value={e.id}>
                  <span>
                    {e.title}.............................................
                    {e.price}
                  </span>
                </p>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MenuList;
