import React, { useState, useRef } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, InputGroup, FormControl } from "react-bootstrap";
import CustomButton from "../Button";

const FormDishes = () => {
  const [formDish, setFormDish] = useState({
    image: "",
    name: "",
    price: "",
  });

  const inputFileRef = useRef();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormDish({ ...formDish, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formDish);
    console.log(inputFileRef.current.files);
  };

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <Form
            style={{
              padding: "25px 0",
              marginTop: "20px",
            }}
            onSubmit={handleSubmit}
          >
            <Form.Group controlId="formGridAddress">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Foto del platillo
              </Form.Label>
              <Form.File
                className="test"
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                  with: "100%",
                }}
                name="image"
                type="file"
                ref={inputFileRef}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formGridReference">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Nombre del platillo
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="name"
                type="text"
                value={formDish.name}
                onChange={handleChange}
                placeholder="Ingresa el nombre"
              />
            </Form.Group>

            <Form inline style={{ padding: "30px 0" }}>
              <Form.Label
                style={{ fontFamily: "GothamBold", paddingRight: "50px" }}
              >
                Metodo de pago
              </Form.Label>
              <Form.Control
                as="select"
                className="my-1 mr-sm-2"
                custom
                style={{ fontSize: "14px" }}
              >
                <option value="cash">Efectivo</option>
                <option value="card">Tarjeta T/C</option>
              </Form.Control>
            </Form>

            <Form.Group controlId="formGridAlias">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Precio del platillo
              </Form.Label>
              <InputGroup>
                <InputGroup.Prepend>
                  <InputGroup.Text
                    style={{
                      fontFamily: "GothamBook",
                      fontSize: "13px",
                      height: " 33px",
                      background: "none",
                    }}
                  >
                    $
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  aria-label="Amount "
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="price"
                  type="number"
                  value={formDish.price}
                  onChange={handleChange}
                  placeholder="Ingresa precio"
                />
                <InputGroup.Append>
                  <InputGroup.Text
                    style={{
                      fontFamily: "GothamBook",
                      fontSize: "13px",
                      height: " 33px",
                      background: "none",
                    }}
                  >
                    .00
                  </InputGroup.Text>
                </InputGroup.Append>
              </InputGroup>
            </Form.Group>

            <CustomButton className="button" type="submit">
              Siguiente
            </CustomButton>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default FormDishes;
