import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Modal, Button } from "react-bootstrap";

const UserModal = ({ modalShow, handleClose, isVerified }) => {
  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col className="button">
          <Button onClick={(show = { modalShow })}>Abrir</Button>
          <Modal
            show={modalShow}
            onHide={handleClose}
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header
              closeButton
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <Modal.Title>
                {isVerified ? "Submitted succesfully" : "Failed to submit"}
              </Modal.Title>
            </Modal.Header>
            <Modal.Footer
              style={{
                display: "flex",
                justifyContent: "center",
              }}
            >
              <Button
                variant={`${isVerified == false ? "danger " : "success"}`}
                onClick={handleClose}
                centered
              >
                {isVerified ? "Ok" : "Try again"}
              </Button>
            </Modal.Footer>
          </Modal>
        </Col>
      </Row>
    </Container>
  );
};

export default UserModal;
