import React, { useState, Component } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, Button } from "react-bootstrap";
import GoogleLogin from "react-google-login";
import CustomButton from "../Button";
import "./styles.css";
import google from "../../assets/icons/google.svg";
import { useGoogleLogin } from "react-google-login";

import Api from "../../services/users";

class LogIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      nameUser: "",
      response: "",
      statusreponse: "",
    };
  }
  async responseGoogle(response) {
    console.log(response);
    if (response.profileObj) {
      const { email, name } = response.profileObj;
      const resApi = await Api.login(email, name);
      console.log(resApi);
      if (resApi.data !== "") {
        const userData = await Api.getUserSession(resApi.data);
        console.log(userData);

        if (userData.data.session.user._id) {
          localStorage.setItem("tokenapp", resApi.data);
          localStorage.setItem("userapp", userData.data.session.user._id);
          localStorage.setItem("typeuser", userData.data.session.user.typeUser);
          if (userData.data.session.user.typeUser === "admin") {
            this.props.history.push(
              `/profile/${userData.data.session.user._id}`
            );
          } else {
            this.props.history.push(`/home/${userData.data.session.user._id}`);
          }
        }
      }
    }
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <Form.Group
              controlId="formBasicPassword"
              style={{
                display: "flex",
                justifyContent: "center",
                padding: "30px 0",
                marginBottom: "40px",
              }}
            >
              <GoogleLogin
                clientId="268029895097-ena92hfakj1dvi8ng9uagllejjfo90b7.apps.googleusercontent.com"
                buttonText="Google"
                onSuccess={this.responseGoogle.bind(this)}
                onFailure={this.responseGoogle.bind(this)}
                cookiePolicy={"single_host_origin"}
                isSignedIn={true}
              />
              {/* <Button onClick={signIn} className="google">
              <img src={google} className="icon"></img>

              <span style={{ fontFamily: "GothamBook" }}>Google</span>
            </Button> */}
            </Form.Group>

            <Form>
              <Form.Group controlId="formBasicEmail">
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  type="email"
                  placeholder="Correo electronico"
                />
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  type="password"
                  placeholder="Contraseña"
                />
              </Form.Group>
            </Form>
            <Button
              className="button"
              style={{
                backgroundColor: "#fb923c",
                border: "none",
                padding: "12px 100px",
                marginTop: "100px",
              }}
              size="lg"
              type="submit"
            >
              Guardar
            </Button>
          </Col>
        </Row>
      </Container>
    );
  }
}
export default LogIn;
