import React, { Component } from "react";
import Select from "react-select";
// import Multi from "./multi";
import { Container, Row, Col } from "react-bootstrap";
import { Form } from "react-bootstrap";
import CustomButton from "../Button";

import axios from "axios";

export class MenuRestaurant extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectDishes: [],

      id: "",
      name: "",
    };
  }

  async getDishes() {
    const result = await axios.get(
      "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/plate/"
    );
    const data = result.data;

    const options = data.map((d) => ({
      value: d.id,
      label: d.name,
    }));

    this.setState({ selectDishes: options });
  }

  handleChange(e) {
    this.setState({ id: e.value, name: e.label });
  }
  componentDidMount() {
    this.getDishes();
  }

  render() {
    console.log(this.state.selectDishes);
    return (
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <Form
              style={{
                padding: "25px 0",
                marginTop: "20px",
              }}
            >
              <Form.Group controlId="formGridReference">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  Nombre del men√∫
                </Form.Label>
                <Form.Control
                  style={{
                    fontFamily: "GothamBook",
                    fontSize: "13px",
                  }}
                  name="name"
                  type="text"
                  placeholder="Ingresa el nombre"
                />
              </Form.Group>
              <div
                style={{
                  fontFamily: "GothamBold",
                  backgroundColor: "#F3F4F6",
                  width: "100%",
                  padding: "10px 0 1px 10px",
                  margin: " 40px 0",
                }}
              >
                <h5 style={{ fontSize: "16px" }}> Primer tiempo</h5>
              </div>
              <Select
                options={this.state.selectDishes}
                onChange={this.handleChange.bind(this)}
                isMulti
              />
              {this.state.value === null
                ? ""
                : this.state.value.map((v) => <h4>{v.label}</h4>)}

              <CustomButton className="button" type="submit">
                Guardar
              </CustomButton>
            </Form>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MenuRestaurant;
