import React, { useState, useEfect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './style.css';

const MenuDetail = () => {
	const [ isClick, setIsClick ] = useState(false);
	const [ details, setDetails ] = useState([
		{
			title: 'Comida Corrida',
			description: 'Incluye postre y agua de jamaica',
			foodItems: [
				{
					title: 'Primer Tiempo',
					option1: 'Sopa de lentejas',
					option2: 'Sopa aguada',
					option3: 'Cosome'
				},
				{
					title: 'Primer Tiempo',
					option1: 'Sopa de lentejas',
					option2: 'Sopa aguada',
					option3: 'Cosome'
				}
			]
		},
		{
			title: 'Comida Corrida',
			description: 'Incluye postre y agua de jamaica',
			foodItems: {
				title: 'Primer Tiempo',
				option1: 'Sopa de lentejas',
				option2: 'Sopa aguada',
				option3: 'Cosome'
			}
		}
	]);

	return (
		<div col-12 id="TabContentWrapper">
			{details.lenght !== 0 ? (
				details.map((detail) => (
					<div>
						<div className="row col-12 justify-content-center">
							<h2>{detail.title}</h2>
							<h6>{detail.description}</h6>
							<div className="container">
								<div className="row title-custom">
									<div>{detail['foodItems'].title}</div>
								</div>
								<div className="row">
									<div className="col-12">
										<div className="form-check">
											<input className="form-check-input check-custom" type="checkbox" value="" id="flexCheckDefault" />
											<label className="form-check-label label-custom" for="flexCheckDefault">
												{detail['foodItems'].option1}
											</label>
										</div>

										<div className="form-check">
											<input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
											<label className="form-check-label label-custom" for="flexCheckDefault">
												{detail['foodItems'].option2}
											</label>
										</div>

										<div className="form-check">
											<input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
											<label className="form-check-label label-custom" for="flexCheckDefault">
												{detail['foodItems'].option3}
											</label>
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>
				))
			) : (
				<p>No hay menús</p>
			)}
		</div>
	);
};

export default MenuDetail;
