import React, { Component } from "react";
import { Container, Row, Col } from "react-bootstrap";
import HeaArr from "../../components/HeaArr";
import axios from "axios";
import Test from "../RestProfile/test";

const url =
  "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/restaurant/1/menus/";

class MenuList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      getDishes: [],
    };
  }
  componentDidMount() {
    axios
      .get(
        url
        // "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/restaurant/1/menus/"
      )
      .then((response) => {
        console.log(response.data.menus);
        this.setState({ getDishes: response.data.menus });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <HeaArr />
          <Col>
            <h3
              style={{
                fontFamily: "GothamBold",
                display: "flex",
                justifyContent: "center",
              }}
            >
              Mis men√∫s
            </h3>
            <Col>
              {this.state.getDishes.map((e) => (
                <p
                  key={e.id}
                  value={e.id}
                  style={{ width: "100%", display: "flex" }}
                >
                  {e.title} ......................................{" "}
                  <p style={{ alignContent: "space-around" }}>
                    ${e.price}
                    .00
                  </p>
                </p>
              ))}
            </Col>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MenuList;
