import React, { useState, useRef } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, Button } from "react-bootstrap";
import CustomButton from "../Button";

// function FormDirection({ callback }) {
//   const [street, setStreet] = useState("");
//   const [ext_number, setExt_number] = useState("");
//   const [int_number, setInt_number] = useState("");
//   const [zip_code, setZip_code] = useState("");
//   const [suburb, setSuburb] = useState("");
//   const [reference, setReference] = useState("");
//   const [alias, setAlias] = useState("");
//   const [phone, setPhone] = useState("");

//   const saveValues = (values) => {
//     setStreet(street);
//     console.log(street);
//     callback(values);
//   };

const FormDirection = () => {
  const [formValues, setFormValues] = useState({
    street: "",
    ext_number: "",
    int_number: "",
    zip_code: "",
    suburb: "",
    reference: "",
    alias: "",
    phone: "",
  });

  const inputFileRef = useRef();

  const handleChange = (e) => {
    const { name, value } = e.target;
    // console.log(name, value);
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formValues);
    console.log(inputFileRef.current.files);
  };

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <Form
            style={{
              padding: "25px 0",
            }}
            onSubmit={handleSubmit}
          >
            <Form.Group controlId="formGridAddress">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Calle
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="street"
                type="text"
                value={formValues.street}
                onChange={handleChange}
                placeholder="Ingresa tu dirección"
              />
            </Form.Group>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridNoExt">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  No. Ext
                </Form.Label>
                <Form.Control
                  style={{
                    fontSize: "13px",
                  }}
                  name="ext_number"
                  type="number"
                  value={formValues.ext_number}
                  onChange={handleChange}
                />
              </Form.Group>

              <Form.Group as={Col} controlId="formGridNoInt">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  No. Int
                </Form.Label>
                <Form.Control
                  style={{
                    fontSize: "13px",
                  }}
                  name="int_number"
                  type="number"
                  value={formValues.int_number}
                  onChange={handleChange}
                />
              </Form.Group>

              <Form.Group as={Col} controlId="formGridCp">
                <Form.Label
                  style={{
                    fontFamily: "GothamBold",
                    fontSize: "15px",
                  }}
                >
                  C.P.
                </Form.Label>
                <Form.Control
                  style={{
                    fontSize: "13px",
                  }}
                  name="zip_code"
                  type="number"
                  value={formValues.zip_code}
                  onChange={handleChange}
                />
              </Form.Group>
            </Form.Row>
            <Form.Group controlId="formGridAddress2">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Colonia
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="suburb"
                type="text"
                value={formValues.suburb}
                onChange={handleChange}
                placeholder="Ingresa tu colonia"
              />
            </Form.Group>
            <Form.Group controlId="formGridReference">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Referencias y/o entre calles
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="reference"
                type="text"
                value={formValues.reference}
                onChange={handleChange}
                placeholder="Ingresa tus referencias"
              />
            </Form.Group>
            <Form.Group controlId="formGridAlias">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Escribe un alias
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="alias"
                type="text"
                value={formValues.alias}
                onChange={handleChange}
                placeholder="Ingresa un alias"
              />
            </Form.Group>
            <Form.Group controlId="formGridPhoneNum">
              <Form.Label
                style={{
                  fontFamily: "GothamBold",
                  fontSize: "15px",
                }}
              >
                Número celular
              </Form.Label>
              <Form.Control
                style={{
                  fontFamily: "GothamBook",
                  fontSize: "13px",
                }}
                name="phone"
                type="number"
                value={formValues.phone}
                onChange={handleChange}
                placeholder="(55) 5555-5555"
              />
              <Form>
                <Form.Group>
                  <Form.File
                    id="exampleFormControlFile1"
                    type="file"
                    ref={inputFileRef}
                  />
                </Form.Group>
              </Form>
            </Form.Group>
            <Button
              className="button"
              style={{
                backgroundColor: "#fb923c",
                border: "none",
                padding: "12px 100px",
              }}
              size="lg"
              type="submit"
            >
              Guardar
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default FormDirection;
