import React, { Component, useEffect, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { ListGroup } from "react-bootstrap";
import HeaArr from "../../components/HeaArr";
import axios from "axios";
import edit from "../../assets/icons/edit.svg";
import trash from "../../assets/icons/delete.svg";

const url =
  "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/restaurant/1/orders/";

class OrderList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      getOrder: [],
      orders: [
        {
          orderOne: [],
        },
        {
          orderTwo: [],
        },
        {
          orderThree: [],
        },
        {
          orderFour: [],
        },
      ],
    };
  }

  componentDidMount() {
    axios
      .get(url)
      .then((response) => {
        console.log(response.data.orders);
        this.setState({ getOrder: response.data.orders });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  toDetail(id) {
    this.props.history.push(`/orders/${id}`);
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-md-center">
          <HeaArr />
          <Col>
            <h3
              style={{
                fontFamily: "GothamBold",
                display: "flex",
                justifyContent: "center",
                marginBottom: "25px",
              }}
            >
              Mis pedidos
            </h3>

            <ListGroup variant="flush">
              {this.state.getOrder.orders &&
                this.state.getOrder.orders.map((e) => (
                  <ListGroup.Item
                    onClick={() => this.toDetail(e.id)}
                    key={e.id}
                    value={e.id}
                    style={{
                      width: "100%",
                      display: "flex",
                      flexDirection: "row",
                      alignItems: "flex-end",
                      justifyContent: "space-between",
                    }}
                  >
                    id {e.id} cliente {e.client}
                    {/* <span>
                      ${e.client}
                      .00
                    </span>
                    <span>{e.plates}</span> */}
                  </ListGroup.Item>
                ))}
            </ListGroup>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default OrderList;
