import React, { useEffect, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Form, FormControl } from "react-bootstrap";
import lupa from "../../assets/icons/search.svg";
import axios from "axios";

const Search = (props) => {
  const [data, setData] = useState([]);
  const [q, setQ] = useState("");
  const { getList } = props;

  useEffect(() => {
    fetch(
      "http://ec2-18-216-57-107.us-east-2.compute.amazonaws.com/api/restaurant/1/detail"
    )
      .then((response) => response.json())
      .then((json) => setData(json));
    console.log(response.data);
  }, []);

  const changeDetected = (e) => {
    console.log(e.target.value);
    getList(e.target.value);
  };

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <Form>
            <FormControl
              style={{
                fontFamily: "GothamBold",
                fontSize: "15px",
                border: "none",
                borderRadius: "20px",
                backgroundColor: "#F3F4F6",
                padding: "25px 60px",
                backgroundImage: `url(${lupa})`,
                backgroundRepeat: "no-repeat",
                backgroundPosition: "left 23px center",
              }}
              type="text"
              // value={search}
              // onChange={handleSearch}
              placeholder="Busca tu colonia "
              className="placeholder"
              onChange={changeDetected}
            />
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default Search;
